import React, { useState, useEffect } from 'react'
import './App.css'


const StarWars = () => {
  const [characters, setCharacters] = useState({})
  const [loading, setLoading] = useState(false)
  const [hasError, setError] = useState({state: false, message: ''})
  //object has metadata like films and planets
  //consider how requests all update state

  const [characterMetadata, setMetadata] = useState({charID: 0, films: [''], starships: [''], vehicles: ['']})
  const characterShape = ({ name, films, ...rest }) => ({ name })
  const shapeFilms = ({title,...rest}) => ({title})
  const shapeStarships =({name,...rest}) => ({name})
  const shapeVehicles =({name,...rest}) => ({name})
  const fetchCharacters = async () => {
    try {
      setLoading(true)

      const charactersToFetch = Array.from(Array(15))

      const promises = charactersToFetch.map(async (item, index, array) => await (await fetch(`https://swapi.co/api/people/${index+1}/`)).json())

      const results = await Promise.all(promises)

      const shapedResults = results.map(result => characterShape(result))
      setCharacters([...shapedResults, ...(characters & characters.length > 0 ? characters : [] )])
      //fetchCharacterMetadata(0)
    } catch(e) {
      setError({state: true, message: e.message})
    }
    setLoading(false)
  }
  const fetchCharacterMetadata = async (characterID) => {
    //Get character films..
    let result = characters[0];

    let shapedFilms = []
    console.log(result)
    const charFilms = result.films
    if (charFilms.length > 0) {
      const filmPromises = charFilms.map(async (item, index, array) => (await fetch(item)).json())
      const filmResults = await Promise.all(filmPromises)
      shapedFilms = filmResults.map(result => shapeFilms(result))
    }

    setMetadata({...characterMetadata, films: [...shapedFilms,...characterMetadata.films]})

    //Get starships..
    let shapedStarships = []
    const charStarships = result.starships
    if (charStarships.length > 0 ) {
      const starshipPromises = charStarships.map(async (item) => (await fetch(item)).json())
      const starshipResults = await Promise.all(starshipPromises)
      shapedStarships = starshipResults.map(result => shapeStarships(result))
    }

    setMetadata({...characterMetadata, starships: [...shapedStarships, ...characterMetadata.starships]})

    //Get character vehicles..
    let shapedVehicles = []
    const charVehicles = result.vehicles
    if (charVehicles.length > 0 ) {
      const vehiclesPromises = charVehicles.map(async ( item) => (await fetch(item)).json())
      const vehiclesResults = await Promise.all(vehiclesPromises)
      const shapedVehicles = vehiclesResults.map(result => shapeVehicles(result))
    }

    setMetadata({...characterMetadata, vehicles: [...shapedVehicles,...characterMetadata.vehicles]})
    console.log(shapedFilms)
    console.log(shapedStarships)
    console.log(shapedVehicles)

    setLoading(false)
  }

  useEffect( () => {
    async function anyNameFunction() {
      await fetchCharacters()
      await fetchCharacterMetadata()
    }
    anyNameFunction()
  },[]);


  return (
      <>
        {loading && <div>Loading</div>}
        {hasError && hasError.state &&
        <div>{hasError.message}</div>}
        {characters && characters.length > 0 && (
            <div bg={'background'}>
              <div className={'Row-Div'}>
                {characters.map((character,index) =>
                    <div key={index}
                         bg={'contentAreaBackground'}
                         textAlign={'left'} p={1} px={4}
                         onClick={() => setMetadata({...characterMetadata, charID: index})}>
                      {character.name}
                    </div>)}
              </div>
              <div className={'Row-Div'}>
                {characterMetadata && (

                    <div bg={'contentAreaBackground'}>

                      {characterMetadata.films && characterMetadata.films.length > 0 &&(characterMetadata.films.map((metaData,index) =>

                          <div key={index}
                               bg={'contentAreaBackground'}
                               textAlign={'left'} p={1} px={4}>
                            {metaData.title}</div>))}

                      {characterMetadata.starships && characterMetadata.starships.length > 0 && characterMetadata.starships.map((metaData,index) =>

                          <div key={index}
                               bg={'contentAreaBackground'}
                               textAlign={'left'} p={1} px={4}>
                            {metaData.name}</div>)}

                      {characterMetadata.vehicles && characterMetadata.vehicles.length > 0 &&characterMetadata.vehicles.map((metaData,index) =>

                          <div key={index}
                               bg={'contentAreaBackground'}
                               textAlign={'left'} p={1} px={4}>
                            {metaData.name}

                          </div>)}
                    </div>)}
              </div>
            </div>
        )
        }
      </>
  )
}


export { StarWars }
