import React, {useEffect, useState} from 'react'
import './App.css'
import Box from "./Custom/Box";
import MetadataBox from "./Custom/MetadataBox";


const StarWars = () => {
  const [characters, setCharacters] = useState({})
  const [loading, setLoading] = useState(false)
  const [hasError, setError] = useState({state: false, message: ''})
  //object has metadata like films and planets
  //consider how requests all update state

  const [characterMetadata, setMetadata] = useState({charID: 0, films: [''], starships: [''], vehicles: ['']})
  const characterShape = ({ name, films, ...rest }) => ({ name });

  const fetchCharacters = async () => {
    try {
      setLoading(true);

      const charactersToFetch = Array.from(Array(15));

      const promises = charactersToFetch.map(async (item, index, array) => await (await fetch(`https://swapi.co/api/people/${index+1}/`)).json())

      const results = await Promise.all(promises);

      const shapedResults = results.map(result => characterShape(result));
      setCharacters([...shapedResults, ...(characters & characters.length > 0 ? characters : [] )])
      //fetchCharacterMetadata(0)
    } catch(e) {
      setError({state: true, message: e.message})
    }
  }

  const fetchCharacterMetadata = async (charID) => {
    //Fetch character data
    const result = await (await fetch(`https://swapi.co/api/people/${charID+1}/`)).json();

    const fetchMetaPromises = async (meta) => {
      if (meta.length > 0) {
          const metaPromises = meta.map(async (item) => (await fetch(item)).json());
          return await Promise.all(metaPromises);

      }
    };
    let shapedFilms = [];
    let shapedStarships = [];
    let shapedVehicles = [];

    //Shaping functions
    const shapeFilms = ({title,...rest}) => ({title});
    const shapeStarships =({name,...rest}) => ({name});
    const shapeVehicles =({name,...rest}) => ({name});

    const filmPromises = await fetchMetaPromises(result.films);
    shapedFilms = filmPromises.map(result => shapeFilms(result));

    const starshipPromises = await fetchMetaPromises(result.starships);
    shapedStarships = starshipPromises.map(result => shapeStarships(result));

    const vehiclePromises = await fetchMetaPromises(result.vehicles);
    shapedVehicles = vehiclePromises.map(result => shapeVehicles(result));

    //Set the Metadata sub keys
    setMetadata((characterMetadata) => ({...characterMetadata, films: [...shapedFilms,...characterMetadata.films]}));
    setMetadata((characterMetadata) => ({...characterMetadata, starships: [...shapedStarships, ...characterMetadata.starships]}));
    setMetadata((characterMetadata) => ({...characterMetadata, vehicles: [...shapedVehicles,...characterMetadata.vehicles]}));

    setLoading(false)
  }
  console.log(characterMetadata.films);
  console.log(characterMetadata.starships);
  console.log(characterMetadata.vehicles);
  useEffect( () => {
    fetchCharacters();
    fetchCharacterMetadata(characterMetadata.charID)
  },[]);


  return (
      <>
        {loading && <div>Loading</div>}
        {hasError && hasError.state &&
        <div>{hasError.message}</div>}
        {characters && characters.length > 0 && !loading && (
            <Box bg={'background'}>
              <div className={'Row-Div'}>
                Characters
                {characters.map((character,index) =>
                    <Box key={index}
                         bg={'contentAreaBackground'}
                         textAlign={'left'} p={1} px={4}
                         onClick={() => setMetadata((characterMetadata) => ({...characterMetadata, charID: index}))}>
                      {character.name}
                    </Box>)}
              </div>
              <div className={'Row-Div'}>
                Metadata
                {characterMetadata && (
                    <Box bg={'contentAreaBackground'}>
                      <b>Films:</b>
                      {characterMetadata.films && characterMetadata.films.length > 0 && characterMetadata.films.map((metaData,index) =>
                          <MetadataBox key={index}>{metaData.title}</MetadataBox>)}
                      <b>Starships:</b>
                      {characterMetadata.starships && characterMetadata.starships.length > 0 && characterMetadata.starships.map((metaData,index) =>
                        <MetadataBox key={index}>{metaData.name}</MetadataBox>)}
                      <b>Vehicles:</b>
                      {characterMetadata.vehicles && characterMetadata.vehicles.length > 0 &&characterMetadata.vehicles.map((metaData,index) =>
                          <MetadataBox key={index}>{metaData.name}</MetadataBox>)}
                    </Box>)}
              </div>
            </Box>
        )
        }
      </>
  )
}


export { StarWars }
